-- Table structure for posts
-- ----------------------------
DROP TABLE IF EXISTS `posts`;
CREATE TABLE `posts`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `thumbnail` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `description` mediumtext CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `content` longtext CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `slug` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  `user_id` int(10) UNSIGNED NULL DEFAULT NULL,
  `category_id` int(11) NULL DEFAULT NULL,
  `view_count` int(11) NULL DEFAULT 0,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `posts_slug_unique`(`slug`) USING BTREE,
  INDEX `posts_title_index`(`title`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Compact;

-- ----------------------------
-- Records of posts
-- ----------------------------
INSERT INTO `posts` VALUES (14, 'Bánh gà', 'images/54520016_1222812224545365_2525146972810117120_n.jpg', '<p>&nbsp;</p>\n<div style=\"margin: 0px; padding: 0px; text-align: justify;\">Nếu như bạn vẫn thắc mắc b&aacute;nh g&agrave; ở đ&acirc;u ngon nhất H&agrave; Nội th&igrave; b&aacute;nh g&agrave; Hồng Mai ch&iacute;nh l&agrave; c&acirc;u trả lời. Những chiếc b&aacute;nh g&agrave; thơm ngon n&oacute;ng gi&ograve;n đ&atilde; trở n&ecirc;n qu&aacute; nổi tiếng v&agrave; trở th&agrave;nh một trong những m&oacute;n ăn vặt h&agrave;ng đầu của c&aacute;c bạn trẻ H&agrave; th&agrave;nh.</div>', '<h2 style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif;\"><span style=\"margin: 0px; padding: 0px; font-size: 17px;\">Kh&aacute;m ph&aacute; b&aacute;nh g&agrave; Hồng Mai - M&oacute;n ăn vặt ngon đảo đi&ecirc;n quyến rũ giới trẻ H&agrave; Th&agrave;nh</span></h2>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />Phố Hồng Mai l&agrave; thi&ecirc;n đường ăn vặt của giới trẻ H&agrave; Th&agrave;nh. Thoạt nh&igrave;n qua, phố Hồng Mai chỉ l&agrave; một con phố nhỏ b&igrave;nh thường như bao con phố kh&aacute;c. Nhưng c&oacute; thể bạn kh&ocirc;ng biết rằng nơi đ&acirc;y hội tụ hầu hết những m&oacute;n ăn vặt đỉnh nhất m&agrave; giới trẻ y&ecirc;u th&iacute;ch. Một số m&oacute;n ăn vặt phổ biến quen mặt tại đ&acirc;y như nem chua r&aacute;n, nộm, m&igrave; g&agrave;, tr&agrave; sữa v&agrave; đặc biệt l&agrave; m&oacute;n b&aacute;nh g&agrave; Hồng Mai. B&aacute;nh g&agrave; Hồng Mai l&agrave; địa chỉ ẩm thực quen thuộc của c&aacute;c bạn trẻ cũng như du kh&aacute;ch khi đến với H&agrave; Nội.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\">&nbsp;</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><img style=\"margin: 5px; padding: 0px; vertical-align: middle; border: 0px;\" title=\"Qu&aacute;n b&aacute;nh g&agrave; Hồng Mai từ l&acirc;u đ&atilde; được giới trẻ H&agrave; Th&agrave;nh y&ecirc;u th&iacute;ch\" src=\"https://www.dulichvietnam.com.vn/data/banh-ga-hong-mai-2.jpg\" alt=\"Qu&aacute;n b&aacute;nh g&agrave; Hồng Mai từ l&acirc;u đ&atilde; được giới trẻ H&agrave; Th&agrave;nh y&ecirc;u th&iacute;ch\" width=\"800\" height=\"800\" /></span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\">Qu&aacute;n b&aacute;nh g&agrave; Hồng Mai từ l&acirc;u đ&atilde; được giới trẻ H&agrave; Th&agrave;nh y&ecirc;u th&iacute;ch</span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />Qu&aacute;n b&aacute;nh g&agrave; Hồng Mai nổi tiếng nằm ngay tại ng&otilde; 172 phố Hồng Mai, du kh&aacute;ch đến đ&acirc;y c&oacute; thể dễ d&agrave;ng t&igrave;m qu&aacute;n bởi đ&acirc;y l&agrave; địa chỉ tập trung rất nhiều thực kh&aacute;ch, biển hiệu dễ thương v&agrave; nằm ở vị tr&iacute; dễ t&igrave;m kiếm.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />Một điểm đặc biệt ở qu&aacute;n b&aacute;nh g&agrave; Hồng Mai l&agrave; đồ ăn l&uacute;c n&agrave;o cũng n&oacute;ng hổi, khi kh&aacute;ch order mới bắt đầu r&aacute;n chứ kh&ocirc;ng r&aacute;n trước. Ch&iacute;nh v&igrave; vậy m&agrave; khi thưởng thức, hầu như hương vị nguy&ecirc;n bản của m&oacute;n ăn được giữ trọn vẹn. Tại qu&aacute;n c&oacute; hai loại b&aacute;nh g&agrave; để thực kh&aacute;ch lựa chọn đ&oacute; l&agrave; b&aacute;nh g&agrave; thường v&agrave; b&aacute;nh g&agrave; c&agrave; ri, t&ugrave;y khẩu vị m&agrave; thực kh&aacute;ch c&oacute; thể gọi m&oacute;n b&aacute;nh với hương vị m&igrave;nh y&ecirc;u th&iacute;ch.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\">&nbsp;</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><img style=\"margin: 5px; padding: 0px; vertical-align: middle; border: 0px;\" title=\"Những dĩa b&aacute;nh g&agrave; thơm nức gi&ograve;n tan khiến bất cứ ai cũng kh&ocirc;ng thể cầm l&ograve;ng\" src=\"https://www.dulichvietnam.com.vn/data/banh-ga-hong-mai-6.jpg\" alt=\"Những dĩa b&aacute;nh g&agrave; thơm nức gi&ograve;n tan khiến bất cứ ai cũng kh&ocirc;ng thể cầm l&ograve;ng\" width=\"800\" height=\"799\" /></span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\">Những dĩa b&aacute;nh g&agrave; thơm nức gi&ograve;n tan khiến bất cứ ai cũng kh&ocirc;ng thể cầm l&ograve;ng</span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />Nhiều người chưa c&oacute; cơ hội thưởng thức m&oacute;n b&aacute;nh n&agrave;y đ&atilde; lầm tưởng b&aacute;nh g&agrave; ch&iacute;nh l&agrave; một miếng g&agrave; tẩm bột v&agrave; chi&ecirc;n l&ecirc;n. Tuy nhi&ecirc;n, b&aacute;nh g&agrave; Hồng Mai ở đ&acirc;y được chế biến cũng kh&aacute; c&ocirc;ng phu. Thịt g&agrave; sau khi được lựa kỹ sẽ đem đi xay nhỏ, trộn với c&aacute;c loại gia vị kh&aacute;c nhau, sau đ&oacute; tạo h&igrave;nh th&agrave;nh những chiếc b&aacute;nh tr&ograve;n dẹp vừa phải. Khi chế biến, người ta sẽ đem b&aacute;nh nh&uacute;ng qua mấy lớp bột v&agrave; chi&ecirc;n ngập dầu đến khi b&aacute;nh c&oacute; m&agrave;u v&agrave;ng đẹp mắt v&agrave; gi&ograve;n rụm.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><br style=\"margin: 0px; padding: 0px;\" /></span></em><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><img style=\"margin: 5px; padding: 0px; vertical-align: middle; border: 0px;\" title=\"Hương vị của b&aacute;nh g&agrave; Hồng Mai rất đậm đ&agrave; v&agrave; vừa miệng\" src=\"https://www.dulichvietnam.com.vn/data/banh-ga-hong-mai-4.jpg\" alt=\"Hương vị của b&aacute;nh g&agrave; Hồng Mai rất đậm đ&agrave; v&agrave; vừa miệng\" width=\"800\" height=\"800\" /></span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\">Hương vị của b&aacute;nh g&agrave; Hồng Mai rất đậm đ&agrave; v&agrave; vừa miệng</span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />B&aacute;nh g&agrave; Hồng Mai nổi tiếng l&agrave; bởi hương vị rất đặc biệt, thịt g&agrave; mềm, thơm, gia vị được n&ecirc;m nếm vừa phải v&agrave; b&aacute;nh l&uacute;c n&agrave;o cũng gi&ograve;n rụm chấm với tương c&agrave;, tương ớt v&agrave; sốt mayonnaise ăn rất đ&atilde;. Cắn miếng b&aacute;nh g&agrave; bạn sẽ cảm nhận ngay được vị thịt g&agrave; n&oacute;ng thơm, chấm k&egrave;m sốt th&igrave; th&ecirc;m vị b&eacute;o ngậy đậm đ&agrave;.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><br style=\"margin: 0px; padding: 0px;\" /></span></em><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><img style=\"margin: 5px; padding: 0px; vertical-align: middle; border: 0px;\" title=\"Những chiếc b&aacute;nh đầy đặn thế n&agrave;y m&agrave; chỉ c&oacute; gi&aacute; 5000đ th&ocirc;i đấy\" src=\"https://www.dulichvietnam.com.vn/data/banh-ga-hong-mai-3.jpg\" alt=\"Những chiếc b&aacute;nh đầy đặn thế n&agrave;y m&agrave; chỉ c&oacute; gi&aacute; 5000đ th&ocirc;i đấy\" width=\"800\" height=\"654\" /></span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><em style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\">Những chiếc b&aacute;nh đầy đặn thế n&agrave;y m&agrave; chỉ c&oacute; gi&aacute; 5000đ th&ocirc;i đấy</span></em></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\">&nbsp;</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\">Một điểm cộng d&agrave;nh cho b&aacute;nh g&agrave; Hồng Mai nữa đ&oacute; ch&iacute;nh l&agrave; ngo&agrave;i hương vị thơm ngon th&igrave; b&aacute;nh kh&aacute; đầy đặn v&agrave; c&oacute; gi&aacute; cực rẻ. Chỉ với 5000đ l&agrave; bạn đ&atilde; c&oacute; thể mua được một chiếc b&aacute;nh g&agrave; Hồng Mai n&oacute;ng hổi, thơm phức rồi.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />B&aacute;nh g&agrave; Hồng Mai vừa c&oacute; thể l&agrave;m m&oacute;n ăn vặt lại vừa c&oacute; thể l&agrave;m m&oacute;n ăn no. Một người b&igrave;nh thường chỉ cần ăn v&agrave;i chiếc b&aacute;nh g&agrave; l&agrave; c&oacute; thể no bụng đến tối. Ngo&agrave;i m&oacute;n b&aacute;nh g&agrave; thần th&aacute;nh th&igrave; tại qu&aacute;n n&agrave;y bạn c&oacute; thể thử m&oacute;n đ&ugrave;i g&agrave; r&aacute;n, nem chua, khoai t&acirc;y chi&ecirc;n, khoai lắc. Nước uống của qu&aacute;n c&oacute; c&aacute;c loại như tr&agrave; đ&agrave;o, tr&agrave; th&aacute;i hoặc coca.</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\">&nbsp;</div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><em style=\"margin: 0px; padding: 0px;\"><img style=\"margin: 5px; padding: 0px; vertical-align: middle; border: 0px;\" title=\"Bạn c&oacute; thể gọi b&aacute;nh k&egrave;m với tr&agrave; tắc, tr&agrave; đ&agrave;o để vừa ăn vừa uống cho đ&atilde;\" src=\"https://www.dulichvietnam.com.vn/data/banh-ga-hong-mai-5.jpg\" alt=\"Bạn c&oacute; thể gọi b&aacute;nh k&egrave;m với tr&agrave; tắc, tr&agrave; đ&agrave;o để vừa ăn vừa uống cho đ&atilde;\" width=\"800\" height=\"800\" /><br style=\"margin: 0px; padding: 0px;\" /></em></span></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: #808080;\"><em style=\"margin: 0px; padding: 0px;\">Bạn c&oacute; thể gọi b&aacute;nh k&egrave;m với tr&agrave; tắc, tr&agrave; đ&agrave;o để vừa ăn vừa uống cho đ&atilde;</em></span></div>\n<div style=\"margin: 0px; padding: 0px; color: #231f20; font-family: Roboto, sans-serif; font-size: 16px; text-align: justify;\"><br style=\"margin: 0px; padding: 0px;\" />Qu&aacute;n b&aacute;nh g&agrave; Hồng Mai kh&aacute; đ&ocirc;ng kh&aacute;ch từ 9h s&aacute;ng đến 19h tối n&ecirc;n một t&iacute;p hữu &iacute;ch cho bạn nếu muốn được thưởng thức m&oacute;n b&aacute;nh hấp dẫn n&agrave;y nhanh, đ&oacute; l&agrave; h&atilde;y tranh thủ đến thật sớm hoặc gọi ship nh&eacute;!</div>', 'banhga', 1, 2, 0, '2019-03-21 14:01:30', '2019-03-22 18:47:12');
INSERT INTO `posts` VALUES (15, 'Xiên nướng', 'images/am-thuc-duong-pho.jpg', '<p><strong style=\"color: #2d2d2d; font-family: \'Noto Serif\', serif; font-size: 16px;\">Thịt xi&ecirc;n nướng</strong><span style=\"color: #2d2d2d; font-family: \'Noto Serif\', serif; font-size: 16px;\">&nbsp;tuy được l&agrave;m từ những nguy&ecirc;n liệu quen thuộc nhưng lại mang đến cho người ăn một hương vị rất đặc biệt. Vị ngon của thịt lợn được quyện c&ugrave;ng c&aacute;c loại gia vị đậm đ&agrave; tạo n&ecirc;n một m&oacute;n ăn m&agrave; c&oacute; lẽ bất kỳ ai cũng đều th&iacute;ch m&ecirc;. Kh&ocirc;ng những thế, với m&oacute;n ăn n&agrave;y, mọi người trong gia đ&igrave;nh bạn c&oacute; thể ngồi sơ chế v&agrave; chế biến c&ugrave;ng nhau mang lại kh&ocirc;ng kh&iacute; qu&acirc;y quần v&ocirc; c&ugrave;ng đầm ấm.</span></p>', '<h3 style=\"box-sizing: border-box; font-family: Roboto, sans-serif; color: #111111; font-weight: 400; margin: 27px 0px 17px; font-size: 22px; line-height: 30px;\"><span id=\"Thit_xien_nuong_8211_nghe_thuat_doi_khi_cung_den_tu_cai_don_gian_nhat\" style=\"box-sizing: border-box;\">Thịt xi&ecirc;n nướng &ndash; nghệ thuật đ&ocirc;i khi cũng đến từ c&aacute;i đơn giản nhất</span></h3>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Điều g&igrave; khiến cho một m&oacute;n ăn d&acirc;n d&atilde; như thịt xi&ecirc;n nướng lại trở n&ecirc;n hấp dẫn v&agrave; nổi tiếng đến mức những thực kh&aacute;ch nước ngo&agrave;i đến Việt Nam chỉ mong muốn được thử ch&uacute;ng?</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Thịt xi&ecirc;n nướng cần phải c&oacute; kỹ năng từ kh&acirc;u chọn thịt đến c&aacute;ch ướp thịt v&agrave; quan trọng nhất l&agrave; c&aacute;ch nướng sao cho xi&ecirc;n thịt đều m&agrave;u v&agrave; ch&iacute;n đều kh&ocirc;ng bị ch&aacute;y.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Đầu ti&ecirc;n l&agrave; c&aacute;ch chọn thịt. Thịt thường được chọn th&iacute;ch hợp cho m&oacute;n ăn th&ecirc;m ngon miệng thường l&agrave; thịt ba chỉ, kh&ocirc;ng qu&aacute; mỡ kh&ocirc;ng qu&aacute; nạc, thường phần thịt nạc sẽ nhiều hơn bởi khi nướng, nếu phần mỡ qu&aacute; nhiều sẽ bị ch&aacute;y trong khi phần thịt nạc chưa kịp ch&iacute;n b&ecirc;n trong. Thường miếng thịt c&oacute; cả nạc v&agrave; mỡ xen kẽ th&igrave; nướng sẽ kh&ocirc;ng bị kh&ocirc; v&agrave; rất vừa vặn. Thịt phải tươi v&agrave; đều m&agrave;u th&igrave; khi nướng sẽ ngọt thịt v&agrave; ăn gi&ograve;n b&ecirc;n ngo&agrave;i mềm b&ecirc;n trong.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-2.jpg\"><img class=\"lazy aligncenter size-full wp-image-8409 td-animation-stack-type0-1 lazy-loaded\" style=\"box-sizing: border-box; border: 0px; max-width: 100%; height: auto; text-align: center; clear: both; display: block; margin: 6px auto 21px;\" src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-2.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-2.jpg 500w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-2-300x200.jpg 300w\" alt=\"Thịt xi&ecirc;n nướng \" width=\"500\" height=\"334\" data-lazy-type=\"image\" data-lazy-src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-2.jpg\" data-lazy-srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-2.jpg 500w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-2-300x200.jpg 300w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" /></a></p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Sau khi chọn thịt để nướng, cần th&aacute;i thịt th&agrave;nh những miếng vu&ocirc;ng vừa phải đều nhau, th&aacute;i d&agrave;y một ch&uacute;t cũng kh&ocirc;ng sao, để khi nướng thịt sẽ quắt bớt phần mỡ thừa, nh&igrave;n miếng thịt xi&ecirc;n nướng trong được đẹp mắt, ngon miệng hơn.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Bước được coi l&agrave; quan trọng gần như nhất của c&aacute;c kh&acirc;u chế biến m&oacute;n thịt xi&ecirc;n nướng đ&oacute; l&agrave; sơ chế nước sốt để ướp thịt. Nhiều h&agrave;ng tiết kiệm thời gian bằng c&aacute;ch sử dụng g&oacute;i ướp thịt b&aacute;n sẵn ở c&aacute;c cửa h&agrave;ng tạp h&oacute;a nhưng c&aacute;ch ướp thịt truyền thống vẫn ngon l&agrave;nh hơn cả l&agrave; chỉ cần một ch&uacute;t đường, dầu h&agrave;o, x&igrave; dầu hoặc nước mắm, c&oacute; thể th&ecirc;m một ch&uacute;t nh&aacute;nh sả đ&atilde; th&aacute;i nhỏ để l&agrave;m dậy m&ugrave;i thịt, Nước sốt ướp thịt cần phải xăm xắp mặt thịt để thịt c&oacute; thể thấm đều. C&oacute; thể th&ecirc;m một ch&uacute;t ớt băm, tỏi băm hoặc h&agrave;nh đ&atilde; đập dập để tăng th&ecirc;m phần hương vị cho m&oacute;n ăn. Thời gian ướp cũng rất quan trọng, n&ecirc;n để từ 10 &ndash; 20 ph&uacute;t th&igrave; l&uacute;c đ&oacute; thịt mới thấm đều c&aacute;c mặt v&agrave; nước sốt đ&atilde; ướp.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">B&acirc;y giờ mới l&agrave; bước quan trọng nhất của m&oacute;n thịt xi&ecirc;n nướng đ&oacute; l&agrave; cần đến những b&agrave;n tay t&agrave;i hoa kh&eacute;o l&eacute;o kh&acirc;u những miếng thịt đ&atilde; ướp đ&uacute;ng độ v&agrave;o những que tre đ&atilde; chuẩn bị sẵn v&agrave; nướng đều tr&ecirc;n mặt bếp than hoa đ&atilde; được thổi rực lửa hồng. Thường c&aacute;c bạn thấy những h&agrave;ng qu&aacute;n thịt xi&ecirc;n nướng vỉa h&egrave;, những người nướng thịt kh&ocirc;ng hề rời khỏi chỗ nướng thịt bởi chỉ cần sơ sẩy qu&ecirc;n bẵng đi l&agrave; thịt sẽ ch&aacute;y rất nhanh.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-3.jpg\"><img class=\"lazy aligncenter size-full wp-image-8410 td-animation-stack-type0-1 lazy-loaded\" style=\"box-sizing: border-box; border: 0px; max-width: 100%; height: auto; text-align: center; clear: both; display: block; margin: 6px auto 21px;\" src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-3.jpg\" sizes=\"(max-width: 500px) 100vw, 500px\" srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-3.jpg 500w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-3-300x213.jpg 300w\" alt=\"Thịt xi&ecirc;n nướng \" width=\"500\" height=\"355\" data-lazy-type=\"image\" data-lazy-src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-3.jpg\" data-lazy-srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-3.jpg 500w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-3-300x213.jpg 300w\" data-lazy-sizes=\"(max-width: 500px) 100vw, 500px\" /></a></p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Th&ocirc;ng thường sẽ nướng khoảng từ 5 đến 7 xi&ecirc;n một l&uacute;c tr&ecirc;n vỉ nướng l&agrave; th&iacute;ch hợp cho người nướng linh hoạt lật thịt cho đều.Thịt sẽ mềm v&agrave; ngon hơn nếu cứ khoảng từ 30 gi&acirc;y đến gần 1 ph&uacute;t l&agrave; đồng loạt lật mặt những xi&ecirc;n nướng, kh&ocirc;ng n&ecirc;n để qu&aacute; l&acirc;u một b&ecirc;n thịt tiếp x&uacute;c với lửa v&agrave; phết một ch&uacute;t mật ong l&ecirc;n bề mặt để tạo lớp thịt b&oacute;ng mượt, cho d&ugrave; ngọn lửa chỉ liu riu nhưng sẽ b&ugrave;ng l&ecirc;n nhanh ch&oacute;ng khi c&oacute; phần mỡ thừa chảy xuống.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Thịt xi&ecirc;n nướng th&iacute;ch hợp nhất khi ăn n&oacute;ng, c&ograve;n g&igrave; tuyệt hơn khi cầm tr&ecirc;n tay miếng thịt xi&ecirc;n n&oacute;ng hổi c&oacute; phủ một lớp tương ớt tạo vị cay nhẹ, mềm mại, đậm vị của thịt nướng đều tay.</p>\n<h3 style=\"box-sizing: border-box; font-family: Roboto, sans-serif; color: #111111; font-weight: 400; margin: 27px 0px 17px; font-size: 22px; line-height: 30px;\"><span id=\"Thit_xien_nuong_hien_nay_va_van_de_an_toan_thuc_pham\" style=\"box-sizing: border-box;\">Thịt xi&ecirc;n nướng hiện nay v&agrave; vấn đề an to&agrave;n thực phẩm</span></h3>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Vốn l&agrave; một m&oacute;n ăn đường phố n&ecirc;n đ&ocirc;i khi kh&ocirc;ng ch&uacute; trọng vấn đề an to&agrave;n vệ sinh thực phẩm th&igrave; thật sự đ&oacute; l&agrave; một mối lo ngại cho những t&iacute;n đồ th&iacute;ch ăn vặt. C&oacute; một điều lu&ocirc;n tr&aacute;i ngược rằng những h&agrave;ng qu&aacute;n thịt xi&ecirc;n nướng vỉa h&egrave; lại c&oacute; c&aacute;i th&uacute; vị hơn v&agrave; được y&ecirc;u th&iacute;ch hơn đối với những cửa h&agrave;ng sạch sẽ v&agrave; c&oacute; kh&ocirc;ng gian ri&ecirc;ng. Ch&iacute;nh v&igrave; th&oacute;i quen tận hưởng kh&ocirc;ng kh&iacute; nhộn nhịp v&agrave; thưởng thức những m&oacute;n ăn nhẹ buổi chiều cũng g&acirc;y kh&ocirc;ng &iacute;t những bất lợi về sức khỏe.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-4.jpg\"><img class=\"lazy aligncenter size-full wp-image-8418 td-animation-stack-type0-1 lazy-loaded\" style=\"box-sizing: border-box; border: 0px; max-width: 100%; height: auto; text-align: center; clear: both; display: block; margin: 6px auto 21px;\" src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-4.jpg\" sizes=\"(max-width: 3872px) 100vw, 3872px\" srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4.jpg 3872w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-300x201.jpg 300w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-768x514.jpg 768w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-1024x685.jpg 1024w\" alt=\"Thịt xi&ecirc;n nướng \" width=\"3872\" height=\"2592\" data-lazy-type=\"image\" data-lazy-src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4.jpg\" data-lazy-srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4.jpg 3872w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-300x201.jpg 300w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-768x514.jpg 768w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-4-1024x685.jpg 1024w\" data-lazy-sizes=\"(max-width: 3872px) 100vw, 3872px\" /></a></p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">N&ecirc;n để m&oacute;n thịt xi&ecirc;n nướng trở th&agrave;nh m&oacute;n ăn đường phố an to&agrave;n v&agrave; sạch sẽ th&igrave; kh&ocirc;ng chỉ những người thưởng thức n&ecirc;n c&oacute; gu lựa chọn hợp l&yacute; những h&agrave;ng qu&aacute;n uy t&iacute;n m&agrave; c&ograve;n ở những người nấu ăn l&agrave;m ra m&oacute;n thịt cũng cần c&oacute; c&aacute;i t&acirc;m của người b&aacute;n, lựa chọn thịt sạch, kh&ocirc;ng gian b&aacute;n h&agrave;ng rộng r&atilde;i th&igrave; thực sự sẽ thu h&uacute;t rất nhiều kh&aacute;ch h&agrave;ng.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">&gt;&gt;&gt;&nbsp;Xem th&ecirc;m:&nbsp;<a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://jamja.vn/blog/cach-nau-thit-lon-kho-tau.html\" target=\"_blank\" rel=\"noopener noreferrer\">C&aacute;ch nấu thịt lợn kho t&agrave;u</a>&nbsp;đặc biệt ngon mềm &ldquo;chuẩn cơm mẹ nấu&rdquo;</p>\n<h3 style=\"box-sizing: border-box; font-family: Roboto, sans-serif; color: #111111; font-weight: 400; margin: 27px 0px 17px; font-size: 22px; line-height: 30px;\"><span id=\"Top_6_dia_chi_quan_thit_xien_nuong_nen_den_nhat_o_Ha_Noi\" style=\"box-sizing: border-box;\">Top 6 địa chỉ qu&aacute;n thịt xi&ecirc;n nướng n&ecirc;n đến nhất ở H&agrave; Nội</span></h3>\n<h4 style=\"box-sizing: border-box; font-family: Roboto, sans-serif; color: #111111; font-weight: 400; margin: 24px 0px 14px; font-size: 19px; line-height: 29px;\"><span id=\"Thit_xien_nuong_8211_Nem_Lui_Hue_8211_An_la_Like\" style=\"box-sizing: border-box;\">Thịt xi&ecirc;n nướng &ndash; Nem Lụi Huế &ndash; Ăn l&agrave; Like</span></h4>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">C&oacute; lẽ h&agrave;ng thịt xi&ecirc;n nướng nhỏ nhắn khuất trong ng&otilde; Xưởng Phim đ&atilde; l&agrave; c&aacute;i t&ecirc;n quen thuộc của hầu hết c&aacute;c bạn trẻ H&agrave; Th&agrave;nh. D&ugrave; qu&aacute;n ng&agrave;y c&agrave;ng đ&ocirc;ng v&agrave; bạn sẽ phải chờ đợi kh&aacute; l&acirc;u nhưng đổi lại cho sự bất tiện đ&oacute; c&oacute; lẽ l&agrave; những đĩa thịt xi&ecirc;n nướng v&agrave;ng &oacute;ng thơm nức c&ograve;n n&oacute;ng hổi ngay tr&ecirc;n đĩa. Thịt c&oacute; một ch&uacute;t ch&aacute;y x&eacute;n cạnh ăn rất gi&ograve;n tan, thịt kh&ocirc;ng cần chấm th&ecirc;m một gia vị n&agrave;o m&agrave; thịt ướp rất vừa vặn, thơm ch&uacute;t sả rất tinh tế.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-Nem-L%E1%BB%A5i-Hu%E1%BA%BF.jpg\"><img class=\"lazy aligncenter size-full wp-image-8414 td-animation-stack-type0-1 lazy-loaded\" style=\"box-sizing: border-box; border: 0px; max-width: 100%; height: auto; text-align: center; clear: both; display: block; margin: 6px auto 21px;\" src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-Nem-L%E1%BB%A5i-Hu%E1%BA%BF.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-Nem-Lụi-Huế.jpg 640w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-Nem-Lụi-Huế-300x284.jpg 300w\" alt=\"Thịt xi&ecirc;n nướng Nem Lụi Huế\" width=\"640\" height=\"605\" data-lazy-type=\"image\" data-lazy-src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-Nem-Lụi-Huế.jpg\" data-lazy-srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-Nem-Lụi-Huế.jpg 640w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-Nem-Lụi-Huế-300x284.jpg 300w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Bạn c&oacute; thể gọi th&ecirc;m tr&agrave; chanh quất ở đ&acirc;y rất đặc sắc m&agrave; kh&ocirc;ng chỗ n&agrave;o c&oacute; vị tr&agrave; thơm như thế, ăn c&ugrave;ng với thịt xi&ecirc;n nướng rất hợp vị, thậm ch&iacute; c&ograve;n l&agrave; m&oacute;n ăn ch&iacute;nh cho một buổi tối m&aacute;t mẻ tụ tập bạn b&egrave;.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><strong style=\"box-sizing: border-box;\">Địa chỉ</strong>: Ng&otilde; 4 Thụy Khu&ecirc;, Ba Đ&igrave;nh, H&agrave; Nội</p>\n<h4 style=\"box-sizing: border-box; font-family: Roboto, sans-serif; color: #111111; font-weight: 400; margin: 24px 0px 14px; font-size: 19px; line-height: 29px;\"><span id=\"Thit_xien_nuong_ba_Nga\" style=\"box-sizing: border-box;\">Thịt xi&ecirc;n nướng b&agrave; Ng&agrave;</span></h4>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><a style=\"box-sizing: border-box; background-color: transparent; color: #4db2ec; text-decoration-line: none;\" href=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-b%C3%A0-Ng%C3%A0.jpg\"><img class=\"lazy aligncenter size-full wp-image-8411 td-animation-stack-type0-1 lazy-loaded\" style=\"box-sizing: border-box; border: 0px; max-width: 100%; height: auto; text-align: center; clear: both; display: block; margin: 6px auto 21px;\" src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Th%E1%BB%8Bt-xi%C3%AAn-n%C6%B0%E1%BB%9Bng-b%C3%A0-Ng%C3%A0.jpg\" sizes=\"(max-width: 640px) 100vw, 640px\" srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-b&agrave;-Ng&agrave;.jpg 640w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-b&agrave;-Ng&agrave;-300x188.jpg 300w\" alt=\"Thịt xi&ecirc;n nướng b&agrave; Ng&agrave;\" width=\"640\" height=\"400\" data-lazy-type=\"image\" data-lazy-src=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-b&agrave;-Ng&agrave;.jpg\" data-lazy-srcset=\"https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-b&agrave;-Ng&agrave;.jpg 640w, https://cdn.jamja.vn/blog/wp-content/uploads/2017/07/Thịt-xi&ecirc;n-nướng-b&agrave;-Ng&agrave;-300x188.jpg 300w\" data-lazy-sizes=\"(max-width: 640px) 100vw, 640px\" /></a></p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\">Đ&atilde; c&oacute; t&ecirc;n tuổi hơn chục năm nay v&agrave; đ&atilde; c&oacute; những fan rất trung th&agrave;nh với những xi&ecirc;n thịt nướng đầy đặn, đậm đ&agrave;, cho d&ugrave; thịt c&oacute; hơi một ch&uacute;t ngọt nhưng đ&ocirc;i khi đ&oacute; l&agrave; một trong những yếu tố thu h&uacute;t kh&aacute;ch của cửa h&agrave;ng. 10 ngh&igrave;n một xi&ecirc;n thịt tuy c&oacute; hơi cao so với những h&agrave;ng qu&aacute;n kh&aacute;c nhưng nằm tr&ecirc;n phố cổ th&igrave; thật sự l&agrave; một c&aacute;i gi&aacute; rất hợp l&yacute; cho kh&ocirc;ng chỉ chất lượng m&agrave; c&ograve;n kh&ocirc;ng gian qu&aacute;n rất nhộn nhịp.</p>\n<p style=\"box-sizing: border-box; font-family: Verdana, Geneva, sans-serif; font-size: 15px; line-height: 26px; margin-top: 0px; margin-bottom: 26px; overflow-wrap: break-word; color: #222222;\"><strong style=\"box-sizing: border-box;\">Địa chỉ</strong>: Số 31 Quang Trung, Ho&agrave;n Kiếm, H&agrave; Nội</p>', 'xiennuong', 54, 2, 0, '2019-03-22 01:02:12', '2019-03-22 18:44:14');
INSERT INTO `posts` VALUES (16, 'Trà đá vỉa hè', 'images/images2.jpg', '<p><span style=\"color: #222222; font-family: Arial, Helvetica, sans-serif; font-size: 16px; font-weight: bold;\">Người H&agrave; Nội uống thức g&igrave; nhiều nhất? Tất nhi&ecirc;n kh&ocirc;ng phải l&agrave; c&agrave; ph&ecirc;. Bia cũng chỉ một số người th&iacute;ch. Thứ đồ uống phổ biến nhất của người H&agrave; Nội l&agrave; tr&agrave; đ&aacute; v&agrave; người ta chủ yếu uống ngo&agrave;i vỉa h&egrave;.</span></p>', '<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\"><img class=\"center-img cms-photo\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important; display: block; margin: auto; max-width: 100%;\" src=\"https://image.anninhthudo.vn/w700/uploaded/170/2018_07_08/tra-da-via-he.jpg\" alt=\"ảnh 1\" data-photo-original-src=\"https://image.anninhthudo.vn/uploaded/170/2018_07_08/tra-da-via-he.jpg\" /></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Tr&agrave; đ&aacute; H&agrave; Nội c&oacute; mu&ocirc;n h&igrave;nh vạn trạng nhưng đặc điểm chung l&agrave; b&igrave;nh d&acirc;n. Hầu như g&oacute;c phố n&agrave;o cũng t&igrave;m thấy đ&ocirc;i ba h&agrave;ng tr&agrave; đ&aacute;. Dụng cụ kh&ocirc;ng cần nhiều lắm, một v&agrave;i c&aacute;i ghế nhựa hoặc đ&ocirc;i c&aacute;i ghế gỗ d&agrave;i, một c&aacute;i b&agrave;n, v&agrave;i c&aacute;i cốc. Thế l&agrave; xong &ldquo;bộ mặt&rdquo; của một qu&aacute;n tr&agrave; đ&aacute;.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\"><strong style=\"box-sizing: border-box; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important;\">&ldquo;Th&ocirc;ng tấn x&atilde; vỉa h&egrave;&rdquo;</strong></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Gọi l&agrave; tr&aacute; đ&aacute; nhưng kh&ocirc;ng nhất thiết chỉ c&oacute; tr&agrave; kh&ocirc;, c&ograve;n c&oacute; tr&agrave; tươi, nh&acirc;n trần, nước vối. Tr&agrave; kh&ocirc; th&igrave; nước v&agrave;ng hoặc xanh nếu l&agrave; loại ngon, tr&agrave; tươi nước xanh nhạt, nước vối v&agrave;ng, chỉ c&oacute; nh&acirc;n trần, m&agrave;u t&iacute;m thẫm gần như quả m&ugrave;ng tơi.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Một h&agrave;ng tr&agrave; đ&aacute; c&oacute; thể đủ tất cả những thứ kể tr&ecirc;n hoặc chỉ c&oacute; một đ&ocirc;i thứ nhưng bất cứ h&agrave;ng n&agrave;o cũng phải c&oacute; tr&agrave; kh&ocirc;. Người H&agrave; Nội uống tr&agrave; theo sở th&iacute;ch c&aacute; nh&acirc;n. C&oacute; người chỉ uống tr&agrave; n&oacute;ng, d&ugrave; m&ugrave;a h&egrave; n&oacute;ng ph&aacute;t sốt, ph&aacute;t ốm th&igrave; vẫn nhất thiết phải l&agrave; một ch&eacute;n tr&agrave; bốc kh&oacute;i mới được. C&aacute;i ch&eacute;n uống tr&agrave; thường gọi l&agrave; ly hoa hồng, n&oacute; nhỏ hơn cốc uống bia nhưng to hơn c&aacute;i ch&eacute;n th&ocirc;ng thường. Đối với những người th&iacute;ch uống tr&agrave; n&oacute;ng, họ cho rằng chỉ c&oacute; tr&agrave; n&oacute;ng mới giữ được hương vị đặc sắc của tr&agrave;, từ m&ugrave;i thơm, m&agrave;u sắc.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Nhiều người th&igrave; kho&aacute;i tr&agrave; đ&aacute; v&agrave; tiếp tục sự bảo thủ của người Bắc. Người uống tr&agrave; đ&aacute; c&oacute; thể uống thứ tr&agrave; n&agrave;y trong những ng&agrave;y lạnh căm căm. Một anh bạn người Nam ra H&agrave; Nội chơi, co ro trong gi&oacute; r&eacute;t, vẫn thấy người bạn Bắc gọi một cốc tr&agrave; đ&aacute; uống ngon l&agrave;nh. Đ&atilde; gọi l&agrave; sở th&iacute;ch th&igrave; rất kh&oacute; giải th&iacute;ch, đơn giản l&agrave; th&iacute;ch th&ocirc;i. Th&iacute;ch th&igrave; kh&ocirc;ng cần l&yacute; do.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Ở qu&aacute;n tr&agrave; đ&aacute; người ta n&oacute;i chuyện g&igrave;? Th&ocirc;i th&igrave; từ chuyện tr&ecirc;n trời dưới biển đến con kiến tha con giun. V&agrave; c&oacute; vẻ người Việt th&iacute;ch nhất chuyện thời sự. Cả một kho th&ocirc;ng tin được trao đổi v&agrave; truyền tải ở qu&aacute;n tr&agrave; đ&aacute;, v&agrave; tất nhi&ecirc;n độ tin cậy kh&ocirc;ng cao. Thế mới c&oacute; c&aacute;i gọi l&agrave; &ldquo;Th&ocirc;ng tấn x&atilde; vỉa h&egrave;&rdquo; v&agrave; &ldquo;H&atilde;ng th&ocirc;ng tấn&rdquo; n&agrave;y chủ yếu bắt nguồn từ những qu&aacute;n tr&agrave; đ&aacute; vỉa h&egrave;.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\"><img class=\"center-img cms-photo\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important; display: block; margin: auto; max-width: 100%;\" src=\"https://image.anninhthudo.vn/w700/uploaded/170/2018_07_08/thanhhuong.jpg\" alt=\"ảnh 2\" data-photo-original-src=\"https://image.anninhthudo.vn/uploaded/170/2018_07_08/thanhhuong.jpg\" /></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\"><strong style=\"box-sizing: border-box; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important;\">Mỗi qu&aacute;n tr&agrave; l&agrave; một n&eacute;t ri&ecirc;ng rất kh&aacute;c</strong></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Chuyện phiếm chỉ l&agrave; một đặc điểm của qu&aacute;n tr&agrave; đ&aacute;. N&oacute; c&ograve;n l&agrave; nơi thư gi&atilde;n, giải kh&aacute;t v&agrave; ngắm phố. Tr&agrave; đ&aacute; tuy b&igrave;nh d&acirc;n nhưng kh&ocirc;ng phải v&igrave; thế m&agrave; kh&ocirc;ng c&oacute; những kho&aacute;i th&uacute;. Hay bởi sự quen thuộc của m&igrave;nh m&agrave; ta tự cho l&agrave; đặc sắc cũng n&ecirc;n. Hồi t&ocirc;i l&agrave; sinh vi&ecirc;n trường Đại học Ngoại ngữ H&agrave; Nội, ở khu vực Thanh Xu&acirc;n Bắc, trong c&aacute;i ng&otilde; nhỏ c&oacute; một &ocirc;ng gi&agrave; chuy&ecirc;n b&aacute;n tr&agrave; rất độc đ&aacute;o.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Độc đ&aacute;o l&agrave; tr&agrave; &ocirc;ng b&aacute;n trong c&aacute;i ng&otilde; s&acirc;u hun h&uacute;t, m&agrave; ch&eacute;n tr&agrave; đặc th&igrave; th&ocirc;i rồi. Đưa l&ecirc;n miệng s&iacute;p một t&iacute; đ&atilde; thấy vị ch&aacute;t ngấm trọn lưỡi. Một người bạn của t&ocirc;i bảo rằng, thậm ch&iacute; &ocirc;ng gi&agrave; c&ograve;n pha những ấm tr&agrave; đặc như cao. Ch&eacute;n tr&agrave; r&oacute;t ra đặc s&aacute;nh như mật, cắm một c&aacute;i tăm v&agrave;o đấy n&oacute; cũng kh&ocirc;ng đổ. Nhưng quả thực, t&ocirc;i chưa từng được nh&igrave;n thấy ấm tr&agrave; đ&oacute; lần n&agrave;o, v&agrave; nếu n&oacute; c&oacute; tồn tại, chắc kh&ocirc;ng uống qu&aacute; được một hớp.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">V&igrave; l&agrave; tr&agrave; ch&aacute;t n&ecirc;n người ngồi qu&aacute;n thường chi&ecirc;u với kẹo lạc, kẹo vừng, kẹo dồi. Hầu như tất cả c&aacute;c qu&aacute;n tr&agrave; vỉa h&egrave; H&agrave; Nội đều c&oacute; m&oacute;n n&agrave;y. Một c&aacute;i lọ nhựa đựng mấy phong kẹo lạc, kẹo vừng, v&agrave;i g&oacute;i thuốc l&aacute;, thuốc l&agrave;o. Kh&aacute;ch ngồi nh&acirc;m nhi những ch&eacute;n tr&agrave; đắng ngọt, nhai một thanh kẹo nhấm nh&aacute;p sự vui th&uacute; rất b&igrave;nh d&acirc;n. Quay lại qu&aacute;n tr&agrave; của &ocirc;ng cụ gi&agrave; ng&agrave;y trước. Trong c&aacute;i ng&otilde; tối lờ mờ ấy, mấy cậu bạn t&ocirc;i mỗi đứa cầm một ch&eacute;n tr&agrave; v&agrave; c&aacute;i kẹo lạc. Vừa ăn vừa th&igrave; thầm n&oacute;i chuyện, qu&aacute;n đ&ocirc;ng, hẹp v&agrave; tối, cảm gi&aacute;c như ngồi trong một nh&agrave; mồ tr&ograve; chuyện với những hồn ma.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">C&oacute; một qu&aacute;n tr&agrave; t&ocirc;i rất th&iacute;ch nằm tr&ecirc;n phố H&agrave;ng Đậu. Qu&aacute;n c&oacute; loại tr&agrave; hương nh&agrave;i rất thơm, điều n&agrave;y g&acirc;y ấn tượng mạnh v&igrave; b&acirc;y giờ đa số qu&aacute;n c&aacute;c nơi d&ugrave;ng loại tr&agrave; b&igrave;nh d&acirc;n rẻ tiền, &iacute;t m&ugrave;i thơm. Nước tr&agrave; thường m&agrave;u v&agrave;ng chứ t&igrave;m được qu&aacute;n tr&agrave; nước m&agrave;u xanh v&agrave; thơm hoa nh&agrave;i th&igrave; hiếm thay. C&aacute;i qu&aacute;n tr&agrave; phố H&agrave;ng Đậu ấy gọi l&agrave; qu&aacute;n cho sang chứ gia t&agrave;i của n&oacute; chỉ c&oacute; một c&aacute;i hộp h&igrave;nh chữ nhật để tất cả dụng cụ cần thiết.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">&nbsp;V&agrave; tối tối, một người đ&agrave;n b&agrave; x&aacute;ch theo một c&aacute;i xuyến bọc trong một c&aacute;i giỏ ủ tr&agrave; từ một con ng&otilde; s&acirc;u hun h&uacute;t đi ra. Một miếng gỗ nhỏ d&ugrave;ng để l&agrave;m b&agrave;n v&agrave; đ&ocirc;i ba c&aacute;i ghế nhựa. Điều đặc biệt l&agrave; thỉnh thoảng một c&ocirc; thiếu nữ rất xinh đẹp ra gi&uacute;p mẹ b&aacute;n tr&agrave; khi mẹ c&ocirc; bận việc. Uống một ch&eacute;n tr&agrave; thơm, được ngắm nh&igrave;n gương mặt c&ocirc; thiếu nữ th&aacute;nh thiện như thi&ecirc;n thần th&igrave; c&ograve;n g&igrave; bằng. C&aacute;i duy&ecirc;n của qu&aacute;n tr&agrave; nhiều khi kh&ocirc;ng phải v&igrave; vị thơm của tr&agrave; m&agrave; đ&ocirc;i khi l&agrave; c&aacute;i duy&ecirc;n tr&ecirc;n m&aacute; người thiếu nữ&hellip;</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; text-align: center; line-height: 2.2rem !important;\"><img class=\"center-img cms-photo\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important; display: block; margin: auto; max-width: 100%;\" src=\"https://image.anninhthudo.vn/w700/uploaded/170/2018_07_08/18.jpg\" alt=\"ảnh 3\" data-photo-original-src=\"https://image.anninhthudo.vn/uploaded/170/2018_07_08/18.jpg\" data-desc=\"Hầu như g&oacute;c phố H&agrave; Nội n&agrave;o cũng t&igrave;m thấy đ&ocirc;i ba h&agrave;ng tr&agrave; đ&aacute; với đặc điểm chung l&agrave; b&igrave;nh d&acirc;n&nbsp;\" /><em style=\"box-sizing: border-box; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important;\">Hầu như g&oacute;c phố H&agrave; Nội n&agrave;o cũng t&igrave;m thấy đ&ocirc;i ba h&agrave;ng tr&agrave; đ&aacute; với đặc điểm chung l&agrave; b&igrave;nh d&acirc;n&nbsp;</em></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\"><strong style=\"box-sizing: border-box; color: inherit; font-size: 1.4rem !important; line-height: 2.2rem !important;\">Th&oacute;i quen b&igrave;nh d&acirc;n, dung dị kh&oacute; bỏ</strong></p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Một qu&aacute;n tr&agrave; nữa m&agrave; t&ocirc;i cho rằng rất đặc sắc. C&aacute;i đặc sắc của qu&aacute;n tr&agrave; n&agrave;y kh&ocirc;ng phải hương vị của tr&agrave; m&agrave; ở l&agrave; vị thế của n&oacute;. Chao &ocirc;i, giữa kh&ocirc;ng gian phố cổ, nh&agrave; mặt tiền n&agrave;o cũng bu&ocirc;n b&aacute;n m&agrave; vẫn c&ograve;n một ng&ocirc;i nh&agrave; trổ ra một c&aacute;i cửa sổ t&iacute; hon. Tất cả nh&agrave; mặt phố H&agrave; Nội đ&atilde; mở cửa th&ocirc;ng to&agrave;n bộ, t&igrave;m thấy những ng&ocirc;i nh&agrave; trổ cửa sổ th&igrave; gần như chuyện cổ t&iacute;ch. Thế m&agrave; c&oacute; ng&ocirc;i nh&agrave; kiểu đ&oacute; tr&ecirc;n phố B&aacute;t Đ&agrave;n. Một c&aacute;nh cửa sổ m&agrave;u xanh mở toang, tr&ecirc;n cửa sổ treo những g&oacute;i bim bim nhiều m&agrave;u sắc, ch&iacute;nh giữa đặt v&agrave;i chai nước, g&oacute;i thuốc l&aacute; v&agrave; k&ecirc; s&aacute;t ch&acirc;n tường l&agrave; một c&aacute;i b&agrave;n nhỏ, hai chiếc ghế m&acirc;y.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Đ&oacute; l&agrave; một qu&aacute;n tr&agrave; giản dị như th&aacute;ch thức tất cả những g&igrave; gọi l&agrave; hiện đại, x&ocirc; bồ kiếm ăn của kh&ocirc;ng gian phố thị. Ng&ocirc;i nh&agrave; đ&atilde; cũ lắm, c&aacute;nh cửa cũng cũ lắm, m&agrave;u sơn đ&atilde; nhạt nh&ograve;a v&agrave; b&ecirc;n trong c&aacute;nh cửa sổ ấy l&agrave; một người đ&agrave;n b&agrave;. B&agrave; b&aacute;n tr&agrave; đ&aacute;, v&agrave;i thứ qu&agrave; nhỏ mọn cho trẻ con. H&igrave;nh ảnh n&agrave;y nếu xuất hiện ở n&ocirc;ng th&ocirc;n th&igrave; kh&ocirc;ng c&oacute; g&igrave; lạ nhưng giữa trung t&acirc;m H&agrave; Nội, trong một con phố cổ th&igrave; lạ lắm.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Kh&aacute;ch ngồi xuống chiếc ghế m&acirc;y thong thả gọi một cốc tr&agrave; đ&aacute; v&agrave; cứ thế b&igrave;nh thản ngắm phố phường bận rộn xu&ocirc;i ngược. Cũng ngay gần đấy c&oacute; thể tr&ocirc;ng thấy một h&agrave;ng người đang xếp h&agrave;ng d&agrave;i chờ ăn b&aacute;t phở ở một h&agrave;ng c&oacute; tiếng. D&ugrave; đ&atilde; tiến l&ecirc;n văn minh rất nhiều nhưng sự bảo thủ của người H&agrave; Nội kh&ocirc;ng mất đi l&agrave; bao. Vẫn ki&ecirc;n nhẫn, tr&igrave; nại theo c&aacute;ch ri&ecirc;ng của m&igrave;nh. Người đ&agrave;n b&agrave; b&aacute;n tr&agrave; đ&aacute; vẫn thong thả, nhẹ nh&agrave;ng đưa ch&eacute;n tr&agrave; ở trong nh&agrave; ra, kh&ocirc;ng việc g&igrave; phải vội v&agrave;ng.</p>\n<p style=\"box-sizing: border-box; margin: 0px 0px 18px; color: #333333; font-family: Arial, Helvetica, sans-serif; line-height: 2.2rem !important;\">Cũng kh&ocirc;ng nhất thiết phải mở cửa th&ocirc;ng nh&agrave; để bu&ocirc;n b&aacute;n, d&ugrave; việc đ&oacute; chắc chắn c&oacute; lợi hơn một qu&aacute;n tr&agrave; b&eacute; x&iacute;u. Nhưng thế mới l&agrave; H&agrave; Nội. Vẫn c&oacute; những người, những nơi như thế. Đ&acirc;y ch&iacute;nh l&agrave; &ldquo;bức tranh cổ điển&rdquo; của một thời - những người đ&agrave;n &ocirc;ng x&uacute;m x&iacute;t ngồi uống tr&agrave; đ&aacute;, h&uacute;t thuốc l&agrave;o tỏa kh&oacute;i m&ugrave; mịt v&agrave; b&agrave;n chuyện tr&ecirc;n trời. D&ugrave; thế giới đ&atilde; tiến xa đến mức n&agrave;o th&igrave; người ta vẫn kh&ocirc;ng bỏ được những th&oacute;i quen giản dị.</p>', 'trada', 53, 16, 0, '2019-03-22 18:48:41', '2019-03-22 18:48:41');
INSERT INTO `posts` VALUES (17, 'Bánh xèo', 'images/download4.jpg', '<p>B&aacute;nh x&egrave;o ng&agrave;y mưa....</p>', '<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">Ng&agrave;y nhỏ, t&ocirc;i m&ecirc; m&oacute;n b&aacute;nh x&egrave;o.</p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">M&ugrave;a nắng th&igrave; đừng mơ v&igrave; mẹ c&ocirc;ng việc t&uacute;i bụi ngo&agrave;i đồng, rảnh đ&acirc;u? Cho n&ecirc;n muốn ăn m&oacute;n b&aacute;nh x&egrave;o phải đợi tới m&ugrave;a mưa. M&agrave; phải mưa dầm mới c&oacute; &ldquo;cơ may&rdquo;. C&ograve;n hỏi, mẹ tham c&ocirc;ng tiếc việc lắm. Mưa dầm, hết phương ra đồng mẹ mới lệnh chị Ba đi xay bột, b&agrave;y khu&ocirc;n. Ấy gọi l&agrave; để trả c&aacute;i nợ&hellip; hứa c&ugrave;ng lũ con m&ecirc; b&aacute;nh x&egrave;o ng&agrave;y n&agrave;o cũng lẵng nhẵng theo đu&ocirc;i nhắc lại nhắc đi. Kh&ocirc;ng sao, chậm c&ograve;n hơn kh&ocirc;ng; thấy mẹ lụi cụi b&ecirc; hỏa l&ograve;, x&uacute;c than l&agrave; anh em t&ocirc;i đứa n&agrave;o cũng nhảy cẫng h&ograve; reo, x&uacute;m lại phụ mẹ v&ocirc; c&ugrave;ng t&iacute;ch cực!</p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">B&aacute;nh x&egrave;o miền Trung đơn giản lắm: chỉ cần bột gạo h&ograve;a nước với t&iacute; mỡ h&agrave;nh tr&aacute;ng khu&ocirc;n l&agrave; xong. Muốn ngon th&igrave; cho th&ecirc;m trứng, thịt nạc băm, t&ocirc;m gạo hay nấm th&aacute;i nhỏ l&ecirc;n tr&ecirc;n. B&aacute;nh đ&oacute; l&agrave; thứ b&aacute;nh x&egrave;o cao cấp, d&agrave;nh cho c&aacute;c &ldquo;đại gia&rdquo;. Lũ nhỏ nh&agrave; ngh&egrave;o ch&uacute;ng t&ocirc;i chỉ cần c&aacute;i b&aacute;nh x&egrave;o mỡ h&agrave;nh hoặc hẹ, tr&ecirc;n rắc v&agrave;i cọng gi&aacute; đậu xanh l&agrave; đủ ngon t&iacute;t mắt. May mắn, nếu t&eacute;p đồng rẻ, mẹ mua th&ecirc;m mớ t&eacute;p về rưới l&ecirc;n tr&ecirc;n l&agrave;m nh&acirc;n (rưới lấy c&oacute; m&agrave; th&ocirc;i), đương nhi&ecirc;n bữa b&aacute;nh đ&oacute; sẽ th&agrave;nh &ldquo;đại tiệc&rdquo;&hellip;</p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333; text-align: center;\"><img style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; max-width: 100%; height: auto !important;\" src=\"http://tuanbaovannghetphcm.vn/uploads/images/So-499--Anh-minh-hoa---Banh-xeo-ngay-mua_800x532.png\" alt=\"So-499--Anh-minh-hoa---Banh-xeo-ngay-mua\" width=\"800\" height=\"532\" /></p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">Đổ b&aacute;nh th&igrave; khu&ocirc;n sắp một h&agrave;ng, chễm chệ tr&ecirc;n 4 c&aacute;i hỏa l&ograve; ch&aacute;y đỏ. Mẹ với chị Ba mỗi người 2 khu&ocirc;n. Cắt đoạn sống l&aacute; chuối hột buộc th&ecirc;m t&uacute;m vải một đầu l&agrave;m c&ugrave;i thoa mỡ nước tr&aacute;ng khu&ocirc;n. M&uacute;c v&aacute; bột đổ chan l&ecirc;n khu&ocirc;n k&ecirc;u x&egrave;o, cầm c&aacute;n khu&ocirc;n nghi&ecirc;ng lắc nhanh cho bột chảy đều khu&ocirc;n xong rưới nh&acirc;n l&ecirc;n, đậy nắp. Liệu chừng b&aacute;nh ch&iacute;n l&agrave; dỡ nắp, cầm c&aacute;n khu&ocirc;n đổ ụp nguy&ecirc;n c&aacute;i b&aacute;nh rớt l&ecirc;n s&agrave;ng. Chẳng cần đợi nguội, lũ nhỏ ngồi chầu hẫu quanh s&agrave;ng đ&atilde; nhanh tay nh&oacute;n lấy, ch&iacute; ch&oacute;e chia nhau. Thổi ph&ugrave; ph&ugrave;, chấm mắm ớt h&iacute;t h&agrave; nhai nuốt, ch&eacute;p m&ocirc;i ch&eacute;p lưỡi khen ngon. B&aacute;nh x&egrave;o n&oacute;ng hổi, b&eacute;o ngậy ăn trong một ng&agrave;y mưa lạnh l&agrave;m sao kh&ocirc;ng ngon? Kết quả: ụp c&aacute;i n&agrave;o ra &ldquo;bốc hơi&rdquo; c&aacute;i đ&oacute; mất tăm. Chị Ba qu&aacute;t: Từ từ, chờ đổ xong lu&ocirc;n thể rồi ăn, tụi m&agrave;y ăn vậy hao ai chịu nổi? Hao thiệt: nguy&ecirc;n thau bột đầu ti&ecirc;n muốn cạn đ&aacute;y m&agrave; c&aacute;i s&agrave;ng đựng b&aacute;nh vẫn trống trơ. Mẹ cười, th&ocirc;i kệ ch&uacute;ng đi. L&acirc;u kh&ocirc;ng c&oacute;, ch&uacute;ng th&egrave;m&hellip; Từ đầu buổi tới giờ mẹ chưa được nếm miếng b&aacute;nh n&agrave;o. Ngo&agrave;i trời lạnh buốt nhưng trong bếp hầm hập hơi than khiến mặt mẹ v&agrave; chị Ba đỏ bừng&hellip;</p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">Sang thau bột thứ 2 th&igrave; tốc độ của mấy c&aacute;i &ldquo;t&agrave;u h&aacute; mồm&rdquo; mới bắt đầu chậm lại. Giờ bụng đ&atilde; no nhưng c&ograve;n tiếc chưa chịu rời s&agrave;ng b&aacute;nh, mắt cứ lom lom. Hiểu &yacute;, mẹ bật cười, tay quệt mồ h&ocirc;i: Th&ocirc;i đứng dậy đi, kh&ocirc;ng ai ăn hết đ&acirc;u. Bột c&ograve;n cả thau kia, đường n&agrave;o ch&uacute;ng m&agrave;y chẳng được th&ecirc;m bụng nữa? Nghe lời hứa chắc của mẹ, anh em t&ocirc;i mới y&ecirc;n t&acirc;m rời &ldquo;trận địa&rdquo;. Mẹ v&agrave; chị Ba vẫn loay hoay đ&aacute;nh vật c&ugrave;ng thau bột to tướng. Từng tiếng &ldquo;x&egrave;o&rdquo;, &ldquo;x&egrave;o&rdquo; vọng ra từ gian bếp nhỏ nghe thật vui tai trong khi ngo&agrave;i trời gi&oacute; bấc vẫn đổ về từng đợt lạnh căm k&egrave;m theo tiếng mưa rền rĩ, sụt s&ugrave;i&hellip;</p>\n<p style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 13px; line-height: 22px; font-family: arial; vertical-align: baseline; color: #333333;\">B&aacute;nh x&egrave;o ăn v&agrave;o m&ugrave;a đ&ocirc;ng giữa tiết trời mưa lạnh quả đ&uacute;ng ngon, ăn chỉ c&oacute; no chứ kh&ocirc;ng bao giờ ớn! Đ&oacute; l&agrave; nhận định bao giờ cũng mang t&iacute;nh &ldquo;nhất tr&iacute; cao&rdquo; của anh em t&ocirc;i. Lạ một điều, đổ xong 2 thau bột kh&ocirc;ng kể phần ăn trong bếp của lũ nhỏ vẫn c&ograve;n một s&agrave;ng b&aacute;nh to; vậy nhưng bữa chiều mẹ chỉ gắp đ&acirc;u hai ba c&aacute;i c&oacute; lệ rồi bu&ocirc;ng đũa đứng l&ecirc;n. T&ocirc;i k&ecirc;u: Mẹ kh&ocirc;ng th&iacute;ch b&aacute;nh x&egrave;o sao? Mẹ cười kh&ocirc;ng đ&aacute;p c&ograve;n chị Ba trừng mắt: Mầy biết c&aacute;i g&igrave; m&agrave; n&oacute;i; thử cho mầy v&ocirc; trong bếp n&oacute;ng hầm hập ngồi từ s&aacute;ng tới tối với mấy c&aacute;i khu&ocirc;n b&aacute;nh bốc um m&ugrave;i dầu mỡ coi mầy c&ograve;n ăn nổi kh&ocirc;ng&hellip;</p>', 'banhxeo', 53, 2, 0, '2019-03-22 19:01:17', '2019-03-22 19:01:17');
